'      IF INSTR(l_hidden_cols, '','' || l_display_col_num || '','') > 0',
'      THEN',
'         --User marked display column as hidden, let''s remove it for them',
'         l_hidden_cols := REPLACE(l_hidden_cols, '','' || l_display_col_num || '','', '''');',
'      END IF;',
'      ',
'      l_hidden_cols := TRIM(BOTH '','' FROM l_hidden_cols);',
