begin
wwv_flow_api.create_plugin(
 p_id=>wwv_flow_api.id(241145552714554620600)
,p_plugin_type=>'ITEM TYPE'
,p_name=>'COM.ORACLEAPEX.DE.TOPET.FRIENDLYLOV_EN'
,p_display_name=>'Friendly LOV (1.2)'
,p_supported_ui_types=>'DESKTOP:JQM_SMARTPHONE'
,p_supported_component_types=>'APEX_APPLICATION_PAGE_ITEMS'
,p_image_prefix => nvl(wwv_flow_application_install.get_static_plugin_file_prefix('ITEM TYPE','COM.ORACLEAPEX.DE.TOPET.FRIENDLYLOV_EN'),'')
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'function makeFriendlyLOV (',
'    p_item                in apex_plugin.t_page_item,',
'    p_plugin              in apex_plugin.t_plugin,',
'    p_value               in varchar2,',
'    p_is_readonly         in boolean,',
'    p_is_printer_friendly in boolean )',
'    return apex_plugin.t_page_item_render_result',
'is',
'    v_LOV                   apex_plugin_util.t_column_value_list;',
'    v_valueExists           boolean;       /* stores whether item-value exists in LOV */',
'    v_displayvalue          varchar2(255);',
'    v_sqlstr                varchar2(4000) := p_item.attribute_01;',
'    v_notificationText      varchar2(255)  := p_item.attribute_02; ',
'	                               /* HTML title and validation error text */',
'                                   /* ...attr_03 used in validation-function: YES/NO */',
'    v_showNotificationIcon  boolean        := (p_item.attribute_04 = ''Y'');',
'    v_showTooltip           boolean        := (p_item.attribute_05 = ''Y'');',
'    v_quicksearch           boolean        := (p_item.attribute_06 = ''Y'');',
'    v_quicksearchlocation   varchar2(1)    := p_item.attribute_07;',
'    v_maxrowcount           number         := nvl(regexp_substr(p_item.attribute_08,''\d+''),500);',
'    ',
' -- MEGAST ######################################### ',
'    v_listaszelesseg        number         := nvl(regexp_substr(p_item.attribute_09,''\d+''),0);',
'    v_minKeresoHossz        number         := nvl(regexp_substr(p_item.attribute_10,''\d+''),0);',
' -- MEGAST ######################################### ',
'    ',
'    v_JSONparams            varchar2(1000);',
'    ',
'    v_result                apex_plugin.t_page_item_render_result;',
'    v_html                  varchar2(32767);',
'    v_css                   varchar2(32767);',
'    v_js                    varchar2(32767);',
'',
'	errline number := 0;',
'begin',
'',
'    if wwv_flow.g_debug then',
'        apex_plugin_util.debug_page_item (',
'        p_plugin              => p_plugin,',
'        p_page_item           => p_item,',
'        p_value               => p_value,',
'        p_is_readonly         => p_is_readonly,',
'        p_is_printer_friendly => p_is_printer_friendly );',
'    end if;',
'    ',
'    ',
'    -- css upload:',
'    APEX_CSS.add_file (',
'            p_name      => ''css_apexplugin_friendlyLOV_item'',',
'            p_directory => p_plugin.file_prefix,',
'            p_version     => null',
'    );',
'    -- js upload:',
'    APEX_JAVASCRIPT.add_library (',
'            p_name        => ''js_apexplugin_friendlyLOV_item'',',
'            p_directory => p_plugin.file_prefix,',
'            p_version     => null',
'    );',
'    ',
'    -- js initialisation of item on page load, if item is not set to "read-only":',
'    if not (instr(nvl(p_item.element_attributes, '' ''), ''readonly="true"'') > 0 ',
'            or instr(nvl(p_item.element_attributes, '' ''), ''readonly="readonly"'') > 0',
'            or p_is_readonly or p_is_printer_friendly ) then',
'			',
'        v_html := ''$(''''#@item@'''').friendlyLOV_pluginItem({ @options@ })'';',
'        v_JSONparams :=''searchLocation:"'' || v_quicksearchlocation || ''",ajaxident:"'' || apex_plugin.get_ajax_identifier || ''"'';',
'',
'		if p_item.lov_cascade_parent_items is not null then',
'			v_JSONparams := v_JSONparams || '',lov_cascade_parent_items:"'' || p_item.lov_cascade_parent_items ',
'						   || ''",ajax_items_to_submit:"'' || p_item.ajax_items_to_submit ',
'						   || ''",ajax_optimize_refresh:'' || case when p_item.ajax_optimize_refresh then ''true'' else ''false'' end;',
'		end if;',
'        -- Both allowNull and quick-search-functionality default to true in js. ',
'        -- So only if not, according to item settings, this options are passed to js as JSON ',
'        if p_item.is_required then',
'            v_JSONparams := v_JSONparams || '',allowNull:false'';',
'        end if;',
'        ',
'        if not v_quicksearch then',
'             v_JSONparams := v_JSONparams || '',quicksearch:false'';',
'        end if;',
'     ',
'     -- MEGAST ######################################### ',
'        if v_listaszelesseg > 0 then ',
'            v_JSONparams := v_JSONparams || '',keresoListaSzelesseg:'' || v_listaszelesseg;',
'        end if;',
'        if v_minKeresoHossz > 0 then ',
'            v_JSONparams := v_JSONparams || '',minKeresoHossz:'' || v_minKeresoHossz;',
'        end if;',
'     -- MEGAST ######################################### ',
'     ',
'        v_html := replace ( v_html, ''@item@'', p_item.name); ',
'        v_html := replace ( v_html, ''@options@'', v_JSONparams); ',
'        APEX_JAVASCRIPT.ADD_ONLOAD_CODE (',
'                p_code => v_html,',
'                p_key  => null -- do this for each item ',
'        );',
'    end if; /* js-init */',
'    ',
'    -- **** preparation ****',
'    errline := 1;',
'    v_LOV := APEX_PLUGIN_UTIL.GET_DATA (',
'                p_sql_statement        => p_item.lov_definition,',
'                p_min_columns          => 2,',
'                p_max_columns          => 2,',
'                p_component_name       => p_item.name,',
'				p_max_rows             => v_maxrowcount',
'            );',
'			',
'    errline := 2;',
'    if p_value is not null then',
'        -- check if value is in LOV and get display value:',
'        v_valueExists := false;',
'        for i in 1..v_LOV(1).count loop',
'        ',
'            if v_LOV(2)(i) = p_value then',
'                v_valueExists := true;',
'                v_displayvalue := v_LOV(1)(i);',
'                exit;',
'            end if;',
'        ',
'        end loop;',
'    ',
'        if not v_valueExists then',
'            -- try get the missing value to display from the 2nd query:',
'            if v_sqlstr is not null then',
'                begin',
'                    v_sqlstr := replace(v_sqlstr, chr(13), '' '');',
'                    execute immediate v_sqlstr into v_displayvalue using p_value;',
'                    ',
'                    EXCEPTION WHEN NO_DATA_FOUND THEN',
'                        null; /* when no result, treat like null-value*/',
'                    WHEN OTHERS THEN ',
'                        -- there must be an error in the query. Display that and quit:',
'                        htp.prn(''<span style="color:Firebrick">Error in query: '' ',
'                                || replace( v_sqlstr,'':1'','''') || p_value || ''<br>'' ',
'                                || sqlerrm || ''</span>'');',
'                        v_result.is_navigable := false;',
'                        return v_result; ',
'                end; -- *end error block*',
'            end if;',
'            ',
'            -- under the condition there is some value, and no matching display:',
'            if v_displayvalue is null and p_item.lov_display_extra then',
'                v_displayvalue := p_value;',
'            end if;',
'        end if;',
'    end if;',
'    ',
'	-- if no value for display has been found:',
'	if v_displayvalue is null and p_item.lov_display_null then',
'		v_displayvalue := p_item.lov_null_text;',
'	end if;',
'	',
'	if p_is_printer_friendly then',
'	    sys.htp.p(''<span>'' || v_displayvalue || ''</span>'');',
'        v_result.is_navigable := true;',
'        return v_result;',
'	end if;',
'	',
'	',
'    -- **** HTML set-up input and select element: ****',
'    -- item to be processed:',
'    v_html := ''<input type="hidden" value="@value@" name="@name@" id="@id@" class="fylov-keyitem">'';',
'                                    /* when standard (= no 2nd query in item definition) AND invalid,',
'                                       replace value with null, else original value: */',
'    v_html := replace ( v_html, ''@value@'',    case when v_sqlstr is null and not v_valueExists ',
'												   and not p_item.lov_display_extra',
'                                                   then '''' else sys.htf.escape_sc(p_value) end);',
'    v_html := replace ( v_html, ''@name@'',     apex_plugin.get_input_name_for_page_item(false) );',
'    v_html := replace ( v_html, ''@id@'',       p_item.name );',
'    sys.htp.p(v_html);',
'	',
'	-- box for display:',
'	sys.htp.p(''<div class="fylov-item-display">'');',
'',
'    -- GUI item for display and user interaction:',
'    v_html := ''<input type="text" value="@value@" id="@id@" class="@cssclass@" size="@size@" @title@ @attr@ readonly="true" autocomplete="off">'';',
'    v_html := replace ( v_html, ''@value@'',    v_displayvalue );',
'    v_html := replace ( v_html, ''@id@'',       p_item.name || ''_DISPLAY'');',
'    v_html := replace ( v_html, ''@cssclass@'', p_item.element_css_classes || '' fylov-item-input ''',
'                                || case when p_value is null or (not v_valueExists and v_sqlstr is null)',
'                                        then ''fylov-item-nullvalue '' end',
'                                || case when not v_valueExists and v_showNotificationIcon ',
'								        then ''fylov-item-warnicon'' end );',
'    v_html := replace ( v_html, ''@size@'',    p_item.element_width );',
'    v_html := replace ( v_html, ''@attr@'',     p_item.element_attributes );',
'    v_html := replace ( v_html, ''@title@'',    case when not v_valueExists and v_showTooltip  ',
'                                        then ''title="'' || v_notificationText || ''"'' else '''' end );',
'    sys.htp.p(v_html);',
'		',
'	-- Select-arrow-button:',
'    sys.htp.p(''<div class="fylov-item-arrwbtn" tabindex="-1"></div>'');',
'	sys.htp.p(''</div>''); ',
'    sys.htp.p(''<div class="fylov-item-list m-font-size-3-0" tabindex="-1" style="display:none">'');',
'',
'	-- Input for quicksearch-filter:',
'	if v_quicksearch then',
'		sys.htp.p(''<div class="fylov-item-search" tabindex="-1"><input type="text" tabindex="-1"><span>&nbsp;</span><img src="'' ',
'				  || p_plugin.file_prefix || ''loading_icon.gif" style="display:none;vertical-align:-3px"></div>'');',
'	end if;',
'	',
'    ',
'    -- **** render complete LOV: ****',
'    sys.htp.p(''<div class="fylov-item-list-inner" tabindex="-1"><ul>'');',
'   ',
'    for i in 1..v_LOV(1).count loop',
'        sys.htp.prn (''<li data-id="'' || v_LOV(2)(i) || ''"><a href="#" tabindex="-1"''',
'						|| case when v_LOV(2)(i) = p_value then ''class="hover"'' end',
'						|| ''>'' || v_LOV(1)(i) || ''</a></li>'');',
'    end loop;',
'',
'    sys.htp.p ( q''!</ul>',
'              --  <span class="fylov-item-noqsmatch">no match</span>',
'                <span class="fylov-item-noqsmatch">nincs találat</span>',
'                </div>',
'            </div>!'' );',
'',
'    v_result.is_navigable := true;',
'    return v_result;',
'	',
'	exception when others then',
'        htp.p(''Errorlevel: '' || errline);',
'        htp.p(sqlerrm);',
'        v_result.is_navigable := true;',
'        return v_result;',
'	',
'end makeFriendlyLOV;',
'',
'',
'---------  callback function  -----------------',
'--  performs server-side quicksearch',
'',
'function callbackFriendlyLOV (',
'    p_item   in apex_plugin.t_page_item,',
'    p_plugin in apex_plugin.t_plugin )',
'    return apex_plugin.t_page_item_ajax_result',
'	is',
'	    v_search		varchar2(100)  	:= apex_application.g_x01;  -- also stores itemID on parent item refresh',
'		v_slengh       	number 			:= length(v_search);',
'		v_callident		varchar2(8)     := trim(to_char(apex_application.g_x02, ''00000000''));',
'		v_sqlstr        varchar2(4000) 	:= rtrim(p_item.lov_definition, '';'');',
'		v_maxrowcount   number;',
'		v_cur          	number;',
'		v_count   	 	number;',
'		v_colname  	   	sys.dbms_sql.desc_tab2;',
'		v_idcol       	varchar2(1000);',
'		v_showcol     	varchar2(1000);',
'		v_pos         	number;    -- determines highlight position',
'		v_pageitems    	sys.dbms_sql.varchar2_table;',
'		v_unused      	number;',
'',
'	    v_result 		apex_plugin.t_page_item_ajax_result;',
'   errline number := 0;',
'   ',
'begin',
