'',
'	-- if updated by parent items, update items in v_sqlstr on the server:',
'	if v_callident = 99999999 then',
'			APEX_UTIL.SET_SESSION_STATE ( p_name => v_search, p_value => apex_application.g_x03 );',
'			v_slengh := 0; -- (no searchterm send)',
'	end if;',
'	',
'	v_cur := sys.dbms_sql.open_cursor;',
'	sys.dbms_sql.parse (c => v_cur, statement => v_sqlstr, language_flag => sys.dbms_sql.native);',
'    ',
'    ',
'	sys.dbms_sql.describe_columns2(c => v_cur, col_cnt => v_count, desc_t  => v_colname);',
'    ',
'	-- parse column names:',
'	v_showcol := v_colname(1).col_name;',
'	v_idcol := v_colname(2).col_name;',
'	',
'	',
'	if v_slengh > 0 then -- if search string has some content:',
'		v_sqlstr := ''select * from (select '' || v_idcol || '','' || v_showcol ',
