'				  || '' where upper('' || v_showcol || '') like ''''%''''||:p_search||''''%'''' order by ''',
'				  || v_showcol || '')  where rownum <= :p_maxrows''; ',
'',
'	else -- just limit the rows:',
'	',
'		v_sqlstr := ''select ''|| v_idcol || '','' || v_showcol ||'' from ('' || v_sqlstr || '')  where rownum <= :p_maxrows''; ',
'	end if;',
'	',
'	errline := 1;',
'  	sys.dbms_sql.parse (c => v_cur, statement => v_sqlstr, language_flag => sys.dbms_sql.native);',
'',
'	-- bind pageitem values',
'	v_pageitems := wwv_flow_utilities.get_binds(v_sqlstr);',
'	for i in 1.. v_pageitems.count loop',
'		sys.dbms_sql.bind_variable(v_cur, v_pageitems(i), v(substr(v_pageitems(i),2)), 1000);',
'	end loop;',
'	',
'	-- set v_maxrowcount depending on item settings:',
'	v_maxrowcount := case when (p_item.attribute_06 = ''Y'') --> Quick Search is enabled',
'						  then nvl(regexp_substr(p_item.attribute_08,''\d+''),500) ',
'	                      else ''50000'' -- arbitrary, not too much for performance issues',
'						  end;',
'	',
'	sys.dbms_sql.bind_variable(v_cur, ''p_maxrows'', v_maxrowcount);',
'',
'	errline := 2;',
'	 ',
'	if v_slengh > 0 then',
'	 -- bindevariablen:',
'		sys.dbms_sql.bind_variable(v_cur, ''p_search'', upper(v_search));',
'		sys.dbms_sql.define_column(v_cur, 3, v_pos);    ',
'	end if;',
'	  ',
'	sys.dbms_sql.define_column(v_cur, 1, v_idcol, 1000); ',
'	sys.dbms_sql.define_column(v_cur, 2, v_showcol, 1000);',
'    ',
'	-- and action',
'	v_unused := sys.dbms_sql.execute(v_cur);',
'	errline := 3;',
'    ',
'	v_count := 0;',
'	htp.prn(''<ul>'');',
'	if v_slengh > 0 then',
'		while sys.dbms_sql.fetch_rows(v_cur) > 0 loop',
'			sys.dbms_sql.column_value(v_cur, 1, v_idcol);',
'			sys.dbms_sql.column_value(v_cur, 2, v_showcol);',
'			sys.dbms_sql.column_value(v_cur, 3, v_pos);',
'			htp.prn(''<li data-id="'' || v_idcol || ''"><a href="#">''',
'				  || substr(v_showcol, 1, v_pos-1) ',
'				  || ''<span class="highlight">''',
'						  || substr(v_showcol, v_pos, v_slengh)',
'						  || ''</span>''',
'						  || substr(v_showcol, v_pos + v_slengh)',
'						  || ''</a></li>'');',
'			v_count := v_count +1;',
'		end loop;',
'',
'	else',
'		while sys.dbms_sql.fetch_rows(v_cur) > 0 loop',
'			sys.dbms_sql.column_value(v_cur, 1, v_idcol);',
'			sys.dbms_sql.column_value(v_cur, 2, v_showcol);',
'			htp.prn(''<li data-id="'' || v_idcol || ''"><a href="#">'' || v_showcol || ''</a></li>'');',
'			v_count := v_count +1;',
'		end loop;',
'	end if;',
'	htp.p(''</ul>'');',
'	',
'	if v_count = 0 then',
'	  htp.p(''<span style="color:#B22222;font-style:italic;padding:3px;">no match</span>'');',
'	',
'	end if;',
'',
'	sys.dbms_sql.close_cursor(v_cur);',
'	return v_result;',
'',
'   exception when others then ',
'      if sys.dbms_sql.is_open(v_cur) then ',
'         sys.dbms_sql.close_cursor(v_cur);',
'      end if;',
'      htp.p( ''Errln:'' || errline );',
