'        v_LOV                  apex_plugin_util.t_column_value_list;',
'        v_result               apex_plugin.t_page_item_validation_result;',
'',
'begin',
'',
'    -- stop if not or nothing to validate:',
'    if not v_validate or p_value is null or v_sqlstr is null then return null; end if;',
'',
'    -- check as before if value is ok:',
