,p_process_name=>'lezaras'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'v_hibaszoveg varchar2(240);',
'j pls_integer;',
'v_kesz_jel pls_integer;',
'v_jelent_kot varchar2(1);',
'v_flp_id number;',
'v_telj_dat date;',
'begin',
'-- behoz adatokat',
'begin',
'select flp_id, telj_dat into v_flp_id, v_telj_dat from ksf_szl_fej',
'where id = :P1123_KSF_ID;',
'exception when others then',
'  v_telj_dat := trunc(sysdate);',
'  v_flp_id := null;',
'end;  ',
'-- számla elkészültre állítás',
'A_SZLA_VEGE.SZLA_SZAMOL(:P1123_KSF_ID);  -- MÉG EGYSZER KISZÁMOL  ',
'-- megvizsgálja a havi forgalmakat, hogy kaphat-e készpénzes számlát',
' j := A_RFORG.ERTEKHATAR_VIZSG(v_flp_id,to_char(v_telj_dat,''YYYYMM''));',
'   if j = 1 then ',
'     :P1123_HIBA := 1; ',
'   end if;  ',
'-- ha nem kaphat kp stzámlát, hibaüzire futunk vele.',
'',
'',
'if :P1123_HIBA = 0 then -- ha átment az értékhatár vizsgálaton',
'  -- számla végi ellenőrzés  ',
'   J := a_szla_vege.szla_jelent_kot(:P1123_KSF_ID, 0, v_hibaszoveg); ',
'  if j > 0 then  -- hiba van, nem lehet elkészült',
'    v_kesz_jel := 2;      ',
'       M_MSG.KEZ_UZENET -- Tőrlést hivott, hibával jött vissza',
'      (''I''            -- I;F;H;V Info; Figyelem; Hiba; ',
'      ,''NKH_SPEC eljárás''       -- Feladat, művelet',
'      ,''SZLA_KEPZ_HIBA''         -- Müvelet',
'      ,''Számla ellenőrzésben hiba, a számla nem lesz elkészült!''||v_hibaszoveg)',
'      ; ',
'      :P1123_HIBA := 1; ',
'  else',
'    v_kesz_jel := 1;',
'  end if;  ',
'   v_kesz_jel:=A_SZLA_VEGE.TET_END(:P1123_KSF_ID,v_kesz_jel,''I'',V_HIBASZOVEG);     ',
'   -- képzett számlát elkészültté tesz, kiszed számlasorszámot',
'   -- visszaír bizonylatszámot és elkészülési jelet I-re teszi',
