'   if v_kesz_jel = 1 then   ',
'     j := a_szla_vege.kso_ir(:P1123_KSF_ID);   ',
'     if j > 0 then  -- hiba van, nem lehet elkészült  ',
'        M_MSG.KEZ_UZENET -- Tőrlést hivott, hibával jött vissza',
'       (''I''            -- I;F;H;V Info; Figyelem; Hiba; ',
'       ,''NKH_SPEC eljárás''       -- Feladat, művelet',
'       ,''SZLA_KEPZ_HIBA''         -- Müvelet',
'       ,''Hiba az életút keletkeztetésekor'')',
'       ;  ',
'       :P1123_HIBA := 1;',
'     end if;  ',
'     J := a_szla_vege.szla_jelent_kot(:P1123_KSF_ID, 1, v_hibaszoveg);  ',
'     if j > 0 then  ',
'        :P1123_HIBA := 1; ',
'       M_MSG.KEZ_UZENET -- Tőrlést hivott, hibával jött vissza',
'      (''I''            -- I;F;H;V Info; Figyelem; Hiba; ',
'       ,''NKH_SPEC eljárás''       -- Feladat, művelet',
'       ,''SZLA_KEPZ_HIBA''         -- Müvelet',
'      ,''Számla ellenőrzésben hiba, a számla nem lesz elkészült!''||v_hibaszoveg)',
'      ;  ',
'     end if; ',
'      begin',
'        select jelent_kot',
'        into v_jelent_kot',
'        from ksf_szl_fej',
'        where id = :P1123_KSF_ID;',
'      exception when others then',
'        v_jelent_kot := null;',
'      end;',
'      if v_jelent_kot = ''I'' then ',
'        -- online xml küldése',
'        j:= a_szla_vege.szamla_xml20(:P1123_KSF_ID,v_hibaszoveg,1);',
